import UIKit
import CoreLocation
enum Plogging {
    // MARK: Use cases
    
    enum UseCase {
        case StartPlogging
    }
    
    enum State {
        case ready
        case doing
        case pause
    }
   
    enum StartRun{
        struct Response {
            var distance: Measurement<UnitLength>
            var location: Location
        }
        
        struct ViewModel {
            var distance: String
            var location: Location
        }
    }
    enum ChangeState {
        struct Request{
            var state: State
        }
    }
    
    enum LocationAuth{
      struct Response{
        
        var status: CLAuthorizationStatus
        var isLocationServiceEnabled: Bool
      }
    }
}
